INSERT INTO `et_question` VALUES ('2', '个体工商户下列支出准', '<QuestionContent>\n  <title>个体工商户下列支出准予扣除的是（ ）</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>税收滞纳金</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>罚金、罚款和被没收财物的损失</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string>在生产经营活动中向金融企业借款的利息支出</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>个人所得税税款</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'C', '0', '0', '0', '1', null, '', '', '');
INSERT INTO `et_question` VALUES ('3', '下列所得，可以减按1', '<QuestionContent>\n  <title>下列所得，可以减按10%的税率征收企业所得税的有（  ）</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>符合条件的小型微利企业取得的所得</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>国家需要重点扶持的高新技术企业取得的所得</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string>居民企业在中国境内取得的所得</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>在中国境内未设立机构、场所的非居民企业，取得的来源于中国境内的所得</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'D', '0', '0', '0', '1', null, '', '', '');
INSERT INTO `et_question` VALUES ('4', '下列有关固定资产加速', '<QuestionContent>\n  <title>下列有关固定资产加速折旧政策，说法错误的是（）。</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>可享受加整折旧 政策的六大行业包括生物药品制造业，专用设备制造业，铁路、船舶、航空航天和其他运输设备制造业，计算机、通信和其他电子设备制造业，仪器仪表制造业，信息传输、软件和信息技术服务业。</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>对所有行业企业2014年1月1日后新购进的专门用于研发的仪器、设备，单位价值不超过100万元的，允许一次性计入当期成本费用在计算应纳税所得额时扣除，不再分年度计算折旧。</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string> 对所有行业企业2014年1月1日后新购进的专门用于研发的仪器、设备，单位价值超过100万元的，可缩短折旧年限或采取加速折旧的方法。</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>所有行业企业只有2014年1月1日后新购进的单位价值不超过5000元的固定资产，允许一次性计入当期成本费用在计算应纳税所得额时扣除，不再分年度计算折旧。</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'D', '0', '0', '0', '1', null, '', '', '');
INSERT INTO `et_question` VALUES ('5', '下列行为不属于企业所', '<QuestionContent>\n  <title>下列行为不属于企业所得税视同销售行为的是(  )。</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>将资产用于市场推广或销售。</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>将资产用行职工奖励或福利。</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string>将资产在境内总机构及其分支机构之间转移。</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>将资产用于股息分配。</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'C', '0', '0', '0', '1', null, '', '', '');
INSERT INTO `et_question` VALUES ('6', '不符合企业所得税有关', '<QuestionContent>\n  <title>不符合企业所得税有关佣金支出税前扣除政策的是（）。</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>企业以现金等非转账方式支付的手续费及佣金不得在税前扣除。</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>财产保险企业按当年全部保费收入扣除退保金等后余额的15%（含本数，下同）计算限额</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string>人身保险企业按当年全部保费收入扣除退保金等后余额的10%计算限额</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>企业不得将手续费及佣金支出计入回扣、业务提成、返利、进场费等费用</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'A', '0', '0', '0', '1', null, '', '', '');
