INSERT INTO `et_question` VALUES ('8', '某查账征收企业所得税', '<QuestionContent>\n  <title>某查账征收企业所得税的居民纳税人，发生了政策性搬迁业务，在2014年企业所得税汇算清缴时不填报《政策性搬迁纳税调整明细表》的情形有（）。</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>2014年为搬迁完成年度</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>从搬迁开始，2014年时搬迁时间满5年</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string>2013年，发生搬迁损失采用分3年均匀扣除方式</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>2014年，取得搬迁收入但未完成搬迁</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'D', '0', '0', '0', '1', null, '', '', '');
