INSERT INTO `et_question` VALUES ('26', ' 小型微利企业享受所', '<QuestionContent>\n  <title> 小型微利企业享受所得税优惠政策，表述正确的是（ ）</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>企业在办理企业所得税年度纳税申报时应同时报送企业当年从业人员人数声明和财务会计报表。</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>报送年度企业所得税纳税申报表的同时向主管税务所提交《享受小型微利企业所得税优惠政策备案表》《企业年度财务报表》《企业情况说明（从事非国家限制或禁止行业）》。</string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string>在年度纳税申报时，应向主管税务机关报送《企业所得税税收优惠事项备案表》《资产负债表》《企业职工名册》《接受派遣工人数及签订的劳务派遣合同》《企业情况说明（从事非限制类和淘汰类项目）》。</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string>通过填报《企业所得税年度纳税申报表（A类，2014年版）的“从业人数”“资产总额（万元）”栏次，履行备案手续，不再另行备案。</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'D', '0', '0', '0', '1', null, '', '', '');
INSERT INTO `et_question` VALUES ('27', '营改增试点纳税人取得', '<QuestionContent>\n  <title>营改增试点纳税人取得的哪些凭证不可以作为增值税扣税凭证（）。</title>\n  <titleImg></titleImg>\n  <choiceList>\n    <entry>\n      <string>A</string>\n      <string>废旧物资发票</string>\n    </entry>\n    <entry>\n      <string>B</string>\n      <string>农产品收购发票 </string>\n    </entry>\n    <entry>\n      <string>C</string>\n      <string> 农产品销售发票和税收缴款凭证</string>\n    </entry>\n    <entry>\n      <string>D</string>\n      <string> 海关进口增值税专用缴款书</string>\n    </entry>\n  </choiceList>\n  <choiceImgList/>\n</QuestionContent>', '1', null, '0', '1', '0', '2015-03-28 11:53:54', 'admin', '2015-03-28 18:08:57', 'A', '0', '0', '0', '1', null, '', '', '');
