mapper.select:
SELECT a.id questionId,a.content,a.answer,a.analysis,a.question_type_id questionTypeId,a.reference referenceName,a.examing_point examingPoint,b.point_id knowledgePointId,GROUP_CONCAT(d.field_name,' > ',c.point_name,' > ', case when a.keyword is null then '' else CONCAT(' 知识关键点：',a.keyword) end) pointName,GROUP_CONCAT(d.field_name) fieldName from et_question a,et_question_2_point b,et_knowledge_point c,et_field d
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and a.question_type_id=#{typeId} and b.point_id=#{pointId}
group by a.id,a.content,a.answer,a.analysis,a.question_type_id,a.reference, a.examing_point,b.point_id order by a.question_type_id,a.id

select a.point_id pointId,a.point_name pointName,a.field_id fieldId,a.memo,a.state,b.field_name fieldName, case when exists(select 1 from et_question_2_point m where m.point_id=a.point_id) then 0 else 1 end removeable from et_knowledge_point a,et_field b
a.field_id=b.field_id
and a.field_id = #{fieldId}

select a.id questionId,a.expose_times exposeTimes, c.point_id pointId,a.difficulty difficulty,a.question_type_id questionTypeId, a.right_times rightTimes, a.wrong_times wrongTimes,a.points point,a.keyword from et_question a,et_question_2_point b ,et_knowledge_point c
a.id=b.question_id and b.point_id=c.point_id
and c.point_id in #{item}

select a.*,c.point_name pointName,d.field_name fieldName,e.name questionTypeName, examing_point examingPoint from et_question a,et_question_2_point b,et_knowledge_point c,et_field d,et_question_type e
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and a.question_type_id=e.id
and c.field_id = #{filter.fieldId}
and c.point_id = #{filter.knowledge}
and a.question_type_id = #{filter.questionType}
and instr(a.name,#{filter.searchParam})<>0
order by a.last_modify desc

mapper.update:
update
et_user_question_history_t set user_question_hist=#{historyStr},
modify_time=#{modifyTime} where user_id=#{userId}

mapper.select:
select a.point_id questionPointId,a.point_name questionPointName, c.question_type_id questionTypeId,d.`name` questionTypeName,count(1) amount from et_knowledge_point a,et_question_2_point b,et_question c,et_question_type d where a.point_id=b.point_id and b.question_id=c.id and c.question_type_id=d.id and a.point_id in
#{item}
group by a.point_id,a.point_name,c.question_type_id,d.`name`

mapper.delete:
delete from et_question_2_tag where tag_id not in

	#{item}

 and question_id=#{questionId}
