mapper.update:
update et_user
truename=#{user.truename}, password=#{user.password}, email=#{user.email}, phone=#{user.phone}, add_date=#{user.create_date}, expire_date=#{user.expire_date}, add_by=#{user.creator}, enabled=#{user.enabled}, field_id=#{user.fieldId}, login_time=#{user.loginTime}, last_login_time=#{user.lastLoginTime}, province=#{user.province}, company=#{user.company}, department=#{user.department}, idcard=#{user.idcard}, address=#{user.address}, zipcode=#{user.zipcode}, tel=#{user.tel}, type=#{user.type}, username=#{user.username} id=#{user.id}
(username = #{user.username} or id=#{user.id})
and password=#{oldpassword}

update et_role
authority=#{authority}, name=#{name},
where id = #{id}

update et_user
iswin=#{iswin}, winlevel=#{winlevel},
where id = #{id}

mapper.select:
select count(1) from et_user 
where 1=1 

	and province=#{province}


	and company=#{company}

 and iswin='1'
